CREATE TABLE IF NOT EXISTS`julitsch_portal`.`visap_upcoming_event_tbl` ( `eventId` INT(11) NOT NULL AUTO_INCREMENT , `author` INT(2) NULL DEFAULT NULL , `event_title` VARCHAR(255) NULL DEFAULT NULL , `event_detail` LONGTEXT NULL DEFAULT NULL , `event_image` VARCHAR(255) NULL DEFAULT NULL , `eorganizer` VARCHAR(255) NULL DEFAULT NULL , `edate` DATE NULL DEFAULT NULL , `etime` TIME NULL DEFAULT NULL , `evenue` VARCHAR(255) NULL DEFAULT NULL , `status` TINYINT(1) NOT NULL DEFAULT '1' , `created_at` DATE NULL DEFAULT NULL , PRIMARY KEY (`eventId`)) ENGINE = InnoDB;